power_user.custom_css:
/* 选项———— */
* {
  --black30a: rgba(235, 250, 230, 1);
  --black70a: rgba(121, 167, 118, 0.7);
  --grey10: rgba(255, 174, 120, 1);
  --grey30a: rgba(255, 254, 213, 0.3);

  /* 预设 */
  --white30a: rgba(83, 157, 79, 0.6);
  --white50a: rgba(83, 157, 79, 0.5);
}

/* 编辑修改框———— */
.edit_textarea {
  background-color: rgba(235, 250, 230, 0.8) !important;
  color: rgba(53, 77, 105, 0.8) !important;
  border: 2px solid #FFFFFF;
}

/* 代码块———— */
/* 禁用 .hljs 的背景颜色和文字颜色 */
.hljs {
  background-color: transparent !important;
  color: inherit !important;
}

/* 大代码块 */
pre {
  font-family: monospace;
  background-color: rgba(16, 96, 112, 0.8);
  color: rgba(210, 232, 208, 0.9);
  padding: 6px;
  border-radius: 10px;
  text-shadow: 0 0 5px rgba(235, 250, 230, 0.4) !important;
}

pre * {
  text-shadow: inherit;
}

/* 代码块滚动条———— */
body.bubblechat .mes_text pre:has(code) {
  /* ▶压缩大小 */
  max-height: 220px !important;
  overflow-y: auto !important;
}

/* 滚动条轨道 */
body.bubblechat .mes_text pre:has(code)::-webkit-scrollbar-track {
  background-color: rgba(235, 250, 230, 0.4);
}

/* 滚动条滑块 */
body.bubblechat .mes_text pre:has(code)::-webkit-scrollbar-thumb {
  background-color: rgba(235, 250, 230, 0.75);
  border-radius: 10px;
}

