custom_css:
/* 选项———— */
* {
    --black30a: rgba(245, 245, 231, 0.85);
    --black70a: rgba(100, 149, 237, 0.5);
    --grey10: rgba(255, 174, 120, 1);
    --grey30a: rgba(255, 254, 213, 0.3); 
    --grey7070a: rgba(84, 69, 0, 0.7);

    /* 预设 */
    --white30a: rgba(111, 153, 193, 0.6);
    --white50a: rgba(111, 153, 193, 0.6);
}

/* 编辑修改框———— */
.edit_textarea {
    background-color: rgba(245, 245, 231, 0.8) !important;
    color: rgba(53, 77, 105, 0.8) !important;
    border: 2px solid #FFFFFF;
}

/* 代码块———— */
/* 禁用 .hljs 的背景颜色和文字颜色 */
.hljs {
    background-color: transparent !important;
    color: inherit !important;
}

/* 大代码块 */
pre {
    font-family: monospace;
    background-color: rgba(70, 84, 114, 0.9);
    color: rgba(193, 216, 226, 0.9);
    padding: 6px;
    border-radius: 10px;
    text-shadow: 0 0 5px rgba(245, 245, 231, 0.3) !important;
}

pre * {
    text-shadow: inherit;
}

/* 代码块滚动条———— */
body.bubblechat .mes_text pre:has(code) {
    /* ▶压缩大小 */
    max-height: 220px !important;
    overflow-y: auto !important;
}

/* 滚动条轨道 */
body.bubblechat .mes_text pre:has(code)::-webkit-scrollbar-track {
    background-color: rgba(245, 245, 230, 0.4);
}

/* 滚动条滑块 */
body.bubblechat .mes_text pre:has(code)::-webkit-scrollbar-thumb {
    background-color: rgba(245, 245, 231, 0.85);
    border-radius: 10px;
}
