custom_css:
/* 底栏QR按键隐藏*/
#send_form:hover 
#qr--bar {    
  height: 40px;    
  transition: all 0.5s ease;
}
#qr--bar {    
  height: 0.1px;
}
/* 保持原有消息框样式 */
body.bubblechat .mes {
    background-color: rgba(225, 233, 220, 0.3);
}

/* 基础文本样式 */
p, div, span, code {
    text-shadow: 0 0 20px #EFEFCD;
}

/* 消息分隔装饰 - 移动端优化 */
.mes:not(:first-child)::after {
    content: '🍀';
    position: absolute;
    right: 10px;
    font-size: 11px;
    color: rgba(255, 255, 255, 0.6);
    margin-top: -8px;
}

.mes:not(:first-child)::before {
    content: '🌱';
    position: absolute;
    left: 10px;
    font-size: 18px;
    color: rgba(255, 255, 255, 0.6);
    margin-top: -8px;
}

/* 保持其他原有样式 */
q {
    text-shadow: 0 0 6px #979D7A, 0 0 4px #EFEFCD, 0 0 12px #FFFFFF;
    font-weight: 600;  /* 添加适度加粗 */
}


.hljs {
    background-color: transparent !important;
    color: #97AD7A !important;
}

.hljs * {
    color: #97AD7A !important;
}

pre {
    background-color: rgba(245, 245, 245, 0.3);
    border-radius: 6px;
    padding: 16px;
    margin: 10px 0;
    font-family: 'Consolas', 'Monaco', monospace;
    font-size: 14px;
    line-height: 1.5;
    color: #778D5A;
    box-shadow: 0 2px 6px rgba(0, 0, 0, 0.1);
    border: 1px solid rgba(0, 0, 0, 0.1);
}

i {
    color: #FFFFFF;
    text-shadow: 2px 2px #95966E;
}

/* 编辑修改框 */
.edit_textarea {
    background-color: rgba(232, 240, 220, 0.3) !important;
    color: rgba(112, 139, 87, 0.7) !important;
}

* {
    --black30a: rgba(120, 135, 102, 0.2);
    --black70a: rgba(150, 165, 85, 0.3);
    --grey30a: rgba(206, 211, 200, 0.2); 

    /* 预设 */
    --white30a: rgba(141, 177, 120, 0.6);
    --white50a: rgba(180, 200, 100, 0.2);
}
